import {Button} from '@sanity/ui'
import React from 'react'
import {fetchSecrets} from '../actions/secrets'
import client from '../clients/SanityClient'
import getPosterSrc from '../util/getPosterSrc'
import styles from './SelectAsset.css'

const PER_PAGE = 200

function createQuery(start = 0, end = PER_PAGE) {
  return `*[_type == "mux.videoAsset"] | order(_updatedAt desc) [${start}...${end}] {_id, playbackId, thumbTime, data}`
}

export default class SelectAsset extends React.Component {
  pageNo = 0

  constructor(props) {
    super(props)

    this.state = {
      assets: [],
      isLastPage: false,
      isLoading: false,
      secrets: null,
    }
  }

  fetchPage(pageNo) {
    const start = pageNo * PER_PAGE
    const end = start + PER_PAGE
    this.setState({isLoading: true})
    return client.fetch(createQuery(start, end)).then((result) => {
      this.setState((prevState) => ({
        isLastPage: result.length === 0,
        assets: prevState.assets.concat(result),
        isLoading: false,
      }))
    })
  }

  componentDidMount() {
    this.fetchPage(this.pageNo)
    fetchSecrets().then(({secrets}) => this.setState({secrets}))
  }

  select(id) {
    const selected = this.state.assets.find((doc) => doc._id === id)
    if (selected) {
      this.props.onSelect(selected)
    }
  }

  handleItemClick = (event) => {
    event.preventDefault()
    this.select(event.currentTarget.getAttribute('data-id'))
  }

  handleItemKeyPress = (event) => {
    if (event.key === 'Enter') {
      event.preventDefault()
      this.select(event.currentTarget.getAttribute('data-id'))
    }
  }
  handleFetchNextPage = () => {
    this.fetchPage(++this.pageNo)
  }

  handleImageError = (event) => {
    const imageElm = event.currentTarget
    imageElm.src =
      'data:image/svg+xml;base64,'
    imageElm.width = 100
    imageElm.height = 100
  }

  render() {
    const {assets, isLastPage, isLoading, secrets} = this.state

    return (
      <div className={styles.root}>
        <div className={styles.imageList}>
          {assets.map((asset) => {
            const size = 80
            const width = 100
            const height = 100
            const posterUrl = getPosterSrc(asset.playbackId, {
              time: asset.thumbTime || 1,
              // eslint-disable-next-line camelcase
              fit_mode: 'smartcrop',
              width: 100,
              height: 100,
              isSigned: asset.data && asset.data.playback_ids[0].policy === 'signed',
              signingKeyId: secrets.signingKeyId || null,
              signingKeyPrivate: secrets.signingKeyPrivate || null,
            })
            return (
              <a
                key={asset._id}
                className={styles.item}
                data-id={asset._id}
                onClick={this.handleItemClick}
                onKeyPress={this.handleItemKeyPress}
                tabIndex={0}
                style={{
                  width: `${(width * size) / height}px`,
                  flexGrow: `${(width * size) / height}`,
                }}
              >
                <i
                  className={styles.padder}
                  style={{paddingBottom: `${(height / width) * 100}%`}}
                />
                <img
                  onError={this.handleImageError}
                  src={posterUrl}
                  className={styles.image}
                  title={asset.filename || asset.playbackId}
                />
              </a>
            )
          })}
        </div>
        <div className={styles.loadMore}>
          {!isLastPage && (
            <Button
              text="Load more"
              mode="ghost"
              tone="primary"
              onClick={this.handleFetchNextPage}
              loading={isLoading}
            />
          )}
        </div>
      </div>
    )
  }
}
